function gfnFisrtLetterLower(n){return n.substr(0,1).toLowerCase()+n.substr(1)}function gfnGetFloat(n){let t=n.val()||"0";return parseFloat(t.replace(/[.]/g,"").replace(/[,]/g,"."))}function gfnFormatFloat(n,t){return n.toFixed(t).replace(/[.]/g,"x").replace(/[,]/g,".").replace(/[x]/g,",")}function gfnUrlMergeParam(n,t,i){i=encodeURIComponent(i);let u=n.indexOf("?");if(u===-1)return n+"?"+t+"="+i;let r=n.indexOf(t+"=",u);if(r===-1)return n+="&"+t+"="+i;else{let u=n.indexOf("&",r);return u===-1?n.substring(0,r)+t+"="+i:n.substring(0,r)+t+"="+i+n.substring(u,n.length)}}function gfnGetDefaultRouteWithoutID(n){let t=0;for(t=n.length;t>=0;t--)if(n[t]==="/")break;return n.substr(0,t+1)}